defmodule AOCTest do
  use ExUnit.Case
  doctest AOC

  def split(x) when is_integer(x), do: x |> Integer.digits
  def split(x) when is_binary(x), do: x |> String.graphemes |> Enum.map(&String.to_integer/1)

  def captcha_test(input, expected, :one) do
    assert AOC.captcha(split(input), 0) == expected
  end

  def captcha_test(input, expected, :two) do
    input = split(input)
    assert AOC.captcha(input, div(Enum.count(input), 2) - 1) == expected
  end

  @input ""

  test :part_1_example_1, do: captcha_test(1122, 3, :one)
  test :part_1_example_2, do: captcha_test(1111, 4, :one)
  test :part_1_example_3, do: captcha_test(1234, 0, :one)
  test :part_1_example_4, do: captcha_test(91212129, 9, :one)

  test :part_1, do: captcha_test(@input, 1141, :one)

  test :part_2_example_1, do: captcha_test(1212, 6, :two)
  test :part_2_example_2, do: captcha_test(1221, 0, :two)
  test :part_2_example_3, do: captcha_test(123425, 4, :two)
  test :part_2_example_4, do: captcha_test(123123, 12, :two)
  test :part_2_example_5, do: captcha_test(12131415, 4, :two)

  test :part_2, do: captcha_test(@input, 950, :two)

  test :ext_1 do
    n = 7
    half = div(n, 2) - 1
    IO.inspect 1_000_000..9_999_999
    |> Enum.map(&Integer.digits/1)
    |> Enum.filter(fn(a) -> Enum.all?(a, fn(n) -> n != 0 end) end)
    |> Enum.filter(fn(a) -> (AOC.captcha(a, 0) == 0) && (AOC.captcha(a, half) == 0) end)
    |> Enum.count
  end
end
